class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int m = queries.length;
        int n = nums.length;
        boolean[] check = new boolean[n];
        List<Set<Integer>> sets = new ArrayList<>(); 
        int count = 0;
        for (int i = 0; i < n; i++) {
            sets.add(new HashSet<>());
            if (nums[i] == 0) {
                check[i] = true;
                count++;
            } else {
                sets.get(i).add(nums[i]);
            }
        }
        if (count == n) {
            return 0;
        }
        for (int i = 0; i < m; i++) {
            int l = queries[i][0], r = queries[i][1], v2 = queries[i][2];
            for (int j = l; j <= r; j++) {
                if (check[j]) {
                    continue;
                }
                Set<Integer> set2 = new HashSet<>();
                for (int v : sets.get(j)) {
                    if (v == v2) {
                        check[j] = true;
                    } else if (v > v2) {
                        set2.add(v - v2);
                    }
                }
                sets.get(j).addAll(set2);
            }
            count = 0;
            for (boolean c : check) {
                if (c) {
                    count++;
                }
            }
            if (count == n) {
                return i + 1;
            }
        }
        return -1;
    }
}

