    int dp(int i,int sum,int[][] q,int j){
        if(sum == 0){
            return i;
        }
        if(i == q.length){
            return Integer.MAX_VALUE;
        }
        if(arr[sum][i] != -1){
            return arr[sum][i];
        }

        int a = q[i][0];
        int b = q[i][1];
        int v = q[i][2];
        if(a <= j && j <= b && sum >= v){
            arr[sum][i] =  Math.min(dp(i+1,sum-v,q,j), dp(i+1,sum,q,j)) ;
        }
        else{
            arr[sum][i] =  dp(i+1,sum,q,j);
        }
