class Solution {
public:
    bool knapsack(int W, vector<int> &wt) {
        int i, j, n = wt.size();
        vector<bool> dp(W + 1, false);
        dp[0] = true;
        for(i = 0; i < n; ++i)
            for(j = W; j; --j)
                if (j >= wt[i])
                    dp[j] = dp[j] || dp[j - wt[i]];
        return dp[W];
    }
    bool func(vector<int>& a, vector<vector<int>>& queries, int k) {
        int i, j, n = a.size(), m = queries.size();
        vector<vector<int>> adj(n);
        while(k--) {
            auto &q = queries[k];
            for(i = q[0]; i <= q[1]; ++i) adj[i].push_back(q[2]);
        }
        for(i = 0; i < n; ++i)
            if (!knapsack(a[i], adj[i]))
                return false;
        return true;
    }
    int minZeroArray(vector<int>& a, vector<vector<int>>& queries) {
        int i, j, n = a.size(), m = queries.size();
        int ans = -1, l = 0, r = m;
        while(l <= r) {
            int k = (l + r) / 2;
            if (func(a, queries, k))
                ans = k, r = k - 1;
            else l = k + 1;
        }
        return ans;
    }
};

