class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        //how many queries to become a zero array
        //subset in the range can be decremented

        //decrement exactly is the tricky part, cannot let it overflow i.e. cannot go greedy
        //for every element > val in the range, you either apply it or you don't

        //can fix an index
        //for each index see what query you have to go until in order for it to work
        //the max of those is your answer
        int worst = INT_MIN;
        bool all_zero = true;
        for(int i = 0; i < nums.size(); i++){
            if(nums[i] != 0){
                all_zero = false;
            }
            std::unordered_map<long long, int> cache;
            auto ans = get_val(i, nums[i], queries, 0, cache);
            if(ans == INT_MAX){
                return -1;
            }
            worst = std::max(worst, ans);
        }
        if(all_zero){
            return 0;
        }
        return worst+1;
    }

    int get_val(const int n_idx, int num, auto& queries, int idx, auto& cache){
        if(num == 0){
            return idx;
        }
        if(idx == queries.size()){
            return INT_MAX;
        }

        long long key = (((num << 11) | idx) << 5) | n_idx;
        if(cache.find(key) != cache.end()){
            return cache[key];
        }

        auto& q = queries[idx];
        // std::cout << q.size() << std::endl;
        // std::cout << queries.size() << std::endl;
        // std::cout << idx << std::endl;
        int s = q[0], e = q[1], v = q[2]; 
        // std::cout << "post" << std::endl;
        if(n_idx < s || n_idx > e || v > num){
            auto ans = get_val(n_idx, num, queries, idx+1, cache);
            cache[key] = ans;
            // std::cout << "postro" << std::endl;
            return ans;
        }
        if(v == num){
            return idx;
        }

        //apply
        int apply = get_val(n_idx, num-v, queries, idx+1, cache);

        int dont = get_val(n_idx, num, queries, idx+1, cache);


        auto ans = std::min(apply, dont);
        cache[key] = ans;
        return ans;
    }
};

