class Solution {
public:
    int recure(vector<vector<int>> &queries, int val, int ind, int q, vector<vector<int>> &dp){
        if(val == 0) return q;
        if(q == queries.size()) return 1e9;

        if(dp[q][val] != -1) return dp[q][val];

        if(queries[q][0] <= ind && queries[q][1] >= ind && queries[q][2] <= val){
            return dp[q][val] = min(recure(queries, val-queries[q][2], ind, q+1, dp), recure(queries, val, ind, q+1, dp));
        }
        return dp[q][val] = recure(queries, val, ind, q+1, dp);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        vector<int> ans(n);
        for(int i=0; i<n; i++){
            //{q, val}
            vector<vector<int>> dp(queries.size(), vector<int>(nums[i]+1, -1));

            ans[i] = recure(queries, nums[i], i, 0, dp);
        }
        int k = 0;
        for(auto val: ans) k = max(k, val);
        return (k == 1e9 ? -1 : k);
    }
};

