class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        auto abc = make_pair(nums, queries);
        bool allZero = true;
        for (int x : nums) {
            if (x != 0) { 
                allZero = false; 
                break; 
            }
        }
        if (allZero) return 0;
        int m = queries.size(), lo = 1, hi = m, ans = -1;
        while (lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            if (check(nums, queries, mid)) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }

    bool check(const vector<int>& nums, const vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);
        for (int j = 0; j < n; j++) {
            dp[j].reset();
            dp[j].set(0, true);
        }
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                dp[j] |= (dp[j] << val);
            }
        }
        for (int j = 0; j < n; j++) {
            if (!dp[j].test(nums[j])) return false;
        }
        return true;
    }
};

