    vector<bool> isSubsetSum(vector<int> &arr, int sum) {
        int n = arr.size();

        vector<bool> ans;
        vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));
        for (int i = 0; i <= n; i++)
            dp[i][0] = true;

            for (int i = 1; i <= n; i++) {

                for (int j = 1; j <= sum; j++) {
                    if (j < arr[i - 1]) {
                        dp[i][j] = dp[i - 1][j]; 
                    }
                    else {
                        dp[i][j] = dp[i - 1][j] || dp[i - 1][j - arr[i - 1]];
                    }
                }
        }
        for(int i = 0; i <= n; i++)
            ans.push_back(dp[i][sum]);
        return ans;
