class Solution {
public:
int solve(int val,int k,int index,vector<vector<int>>&q,vector<vector<int>>&dp){
    if(val==0) return 1;
    if(val<0) return 0;
    if(k<0) return 0;
    if(dp[val][k]!=-1) return dp[val][k];
    int l= q[k][0];
    int h=q[k][1];
    int v= q[k][2];
    int ans=0;
    if(index>=l && index<=h){
        if(solve(val-v,k-1, index,q,dp)==1) ans=1;
    }
    if(solve(val,k-1, index,q,dp)==1) ans=1;
    return dp[val][k]= ans;
}
bool isPossible(vector<int>&nums,vector<vector<int>>&q,int mid){
    for(int i=0;i<nums.size();i++){
        vector<vector<int>>dp(nums[i]+1,vector<int>(mid,-1));
        if(solve(nums[i],mid-1,i,q,dp)==0){
            return false;
        }
    }
    return true;
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l=0,h=queries.size();
        while((h-l)>1){
            int mid= l+(h-l)/2;
            if(isPossible(nums,queries,mid)){
