            for j, (L, R, v) in enumerate(queries):
                if L <= i <= R:
                    for k in range(num, v-1, -1):
                        dp[k] = dp[k] or dp[k - v]
                if dp[num]:
                    res = max(res, j + 1)
                    break
            if not dp[num]:
                return -1

        return res

