class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        var nonzerocnt=0;
        int n=nums.length;
        int qn=queries.length;
        for (int i=0;i<n;i++) if (nums[i]!=0) nonzerocnt++;
        if (nonzerocnt==0) return 0;
        var l=new ArrayList<Set<Integer>>();
        for (int i=0;i<n;i++) {
            l.add(new HashSet<Integer>());
            if (nums[i]!=0) l.get(i).add(nums[i]);
        }

        for (int i=0;i<qn;i++) {
            int left=queries[i][0];
            int r=queries[i][1];
            int v=queries[i][2];

            for (int j=left;j<=r;j++) {
                if (nums[j]==0) continue;
                if (l.get(j).contains(v)) {
                    l.get(j).clear();
                    nums[j]=0;
                    nonzerocnt--;
                    if (nonzerocnt==0) return i+1;
                } else {
                    var oldvals = new HashSet<Integer>(l.get(j));
                    for (var w: oldvals) {
                        if (w>v) l.get(j).add(w-v);
                    }
                }
            }
        }
        return -1;
    }
}

