                for (int j = val; j <= nums[i]; ++j) {
                    if (dp[i][j])
                        dp[i][j - val] = true;
                }
            }
            bool good = true;
            for (int i = 0; i < n; ++i)
                if (!dp[i][0]) good = false;
            if (good) return ans;
        }
        return -1;
    }
};

