                    ArrayList<Integer> vals = new ArrayList<>(set);
                    for (int v: vals) {
                        set.add(v + query[2]);
                    }
                }
                if (set.contains(target)) {
                    ks[i] = j + 1;
                    break;
                }
            }
            if (ks[i] == 0) {
               return -1;
            }
        }
        int max = 0;
        for (int k: ks) {
            max = Math.max(max, k);
        }
        return max;
    }
}

