class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        def isPossible(k):
            for i in range(n):
                target = nums[i]
                values = []
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        values.append(v)
                p = {0}
                for v in values:
                    np = set(p)
                    for s in p:
                        if s + v <= target:
                            np.add(s + v)
                    p = np
                    if target in p:
                        break
                if target not in p:
                    return False
            return True
        if sum(nums) == 0:
            return 0
        left, right = 1, len(queries)
        res = -1
        while left <= right:
            mid = left + (right - left)//2
            if isPossible(mid):
                res = mid 
                right = mid - 1
            else:
                left = mid + 1
        return res

