class Solution {
    public int minZeroArray(int[] nums, int[][] q) {
        int n = nums.length, m = q.length, max = 0;
        boolean[][] v = new boolean[n][];
        int[] mins = new int[n];
        for (int i = 0; i < n; i++) {
            v[i] = new boolean[nums[i] + 1];
            v[i][0] = true;
            if (nums[i] != 0) mins[i] = -1;
        }
        for (int i = 0; i < m; i++) {
            for (int j = q[i][0]; j <= q[i][1]; j++) {
                for (int k = v[j].length - q[i][2] - 1; k >= 0; k--) {
                    if (v[j][k]) v[j][k + q[i][2]] = true;
                }
                if (v[j][v[j].length - 1] && mins[j] == -1) mins[j] = i + 1;
            }
        }
        for (int i = 0; i < n; i++) {
            if (mins[i] == -1) return -1;
            max = Math.max(max, mins[i]);
        } 
        return max;
    }
}

