class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        @cache
        def dp(qi, ni, num):
            if num == 0:
                return qi
            if qi >= len(queries):
                return -1
            l, r, val = queries[qi]
            if ni < l or r < ni or val > num:
                return dp(qi + 1, ni, num)
            res0 = dp(qi + 1, ni, num)
            res1 = dp(qi + 1, ni, num - val)
            if res0 == -1:
                return res1
            elif res1 == -1:
                return res0
            else:
                return min(res0, res1)
        ans = 0
        for i, num in enumerate(nums):
            k = dp(0, i, num)
            if k == -1:
                return -1
            ans = max(ans, k)
        return ans

