class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        coverage = [ [] for _ in range(n) ] 

        def can_form_target(vals, target):
            if sum(vals) < target:
                return False
            vals.sort(reverse=True)

            def backtrack(i, cur_sum):
                if cur_sum == target:
                    return True
                if i == len(vals) or cur_sum > target:
                    return False
                if backtrack(i+1, cur_sum + vals[i]):
                    return True
                return backtrack(i+1, cur_sum)

            return backtrack(0, 0)

        def canZero(k):
            coverage = [[] for _ in range(n)]
            for i in range(k):
                l_i, r_i, val_i = queries[i]
                for j in range(l_i, r_i+1):
                    coverage[j].append(val_i)

            # For each index j, we need to see if there's a subset of coverage[j]
            # that sums exactly to nums[j].
            for j in range(n):
                x = nums[j]
                if x == 0:
                    continue
                if not can_form_target(coverage[j], x):
                    return False
            return True

        left, right = 0, m+1   # right = m+1 so we can test if left>m eventually
        while left < right:
            mid = (left + right) // 2
            if canZero(mid):
                right = mid
            else:
                left = mid + 1

        # left is the smallest k s.t. canZero(k) is True, if it <= m
        if left <= m:
            return left
        else:
            return -1

