class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        """
        if i find for each positions in how many steps it will be zero then done nah max one is answer
        """
        # @cache
        # @cache
        dp={}
        def do(pos,val,q_p):
            if((pos,val,q_p) in dp):
                return dp[(pos,val,q_p)]
            if(val==0):
                return 0
            if(val<0 or q_p==len(queries)):
                return float('inf')
            p,q,r=queries[q_p]
            ans=1+do(pos,val,q_p+1)
            if(pos<=q and pos>=p):
                ans=min(ans,1+do(pos,val-r,q_p+1))
            dp[(pos,val,q_p)]=ans
            return ans
        res=-1
        for i in range(len(nums)):
            res=max(res,do(i,nums[i],0))
            dp={}
        return -1 if res==float('inf') else res

