class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        List<List<Boolean>> dp = new ArrayList<>();
        int zeros = 0;
        for (int i = 0; i < n; i++) {
            dp.add(Arrays.asList(new Boolean[nums[i] + 1]));
            dp.get(i).set(nums[i], true);
            if (nums[i] == 0) {
                zeros++;
            }
        }
        if (zeros == n) {
            return 0;
        }
        for (int i = 0; i < m; i++) {
            int val = queries[i][2], l = queries[i][0], r = queries[i][1];
            for (int j = l; j <= r; j++) {
                if (dp.get(j).get(0) != null && dp.get(j).get(0) == true) {
                    continue;
                }
                for (int k = 0; k <= nums[j] - val; k++) {
                    if (dp.get(j).get(k + val) != null && dp.get(j).get(k + val) == true) {
                        dp.get(j).set(k, true);
                    }
                }
                if (dp.get(j).get(0) != null && dp.get(j).get(0) == true) {
                    zeros++;
                }
            }

            if (zeros == n) {
                return i + 1;
            }
        }
        // System.out.println(dp);

        return -1;
    }
}

