class Solution {
public:
    int f(int i , int num , int ref , vector<vector<int> > & qe , vector<vector<int> > & dp){
        if(num == 0){
            return 0;
        }
        int q = qe.size();
        if(i >= q){
            return (int)1e7;
        }
        if(dp[i][num] != -1){
            return dp[i][num];
        }
        int ans = f(i + 1 , num , ref , qe , dp) + 1;
        if(qe[i][0] <= ref && ref <= qe[i][1] && qe[i][2] <= num){
            ans = min(ans , f(i + 1 , num - qe[i][2] , ref , qe , dp) + 1);
        }
        return dp[i][num] = ans;
    }
    int minZeroArray(vector<int>& v, vector<vector<int>>& qe) {
        int n = v.size();
        int q = qe.size();
        vector<int> mn_ops_for_i(n);
        for(int i = 0 ; i < n ; i ++){
            {
                int sum = 0;
                for(int k = 0 ; k < q ; k ++){
                    if(qe[k][0] <= i && i <= qe[k][1]){
                        sum += qe[k][2];
                    }
                }
                if(sum < v[i]){
                    return -1;
                }
            }
            vector<vector<int> > dp(q , vector<int> (v[i] + 1 , -1));
            mn_ops_for_i[i] = f(0 , v[i] , i , qe , dp);
        }
        int ans = 0;
        for(int i = 0 ; i < n ; i ++){
            ans = max(ans , mn_ops_for_i[i]);
        }
        if(ans >= 1e7){
            return -1;
        }
        return ans;
    }
};

