class Solution {
public:
    int dpf(int i , int ele,int eleind, vector<vector<int>>&dp, vector<vector<int>>&queries){
        if(ele==0) return i;
        if(i==queries.size() || ele < 0) return INT_MAX;
        if(dp[i][ele]!=-1) return dp[i][ele];
        int take = INT_MAX;
        if(queries[i][0]<=eleind && queries[i][1]>=eleind){
            take = dpf(i+1,ele-queries[i][2],eleind, dp, queries);
        }
        int nottake = dpf(i+1,ele,eleind,dp,queries);
        dp[i][ele] = min(take,nottake);
        return dp[i][ele];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        for(int i = 0; i < nums.size(); i++){
            int ele  = nums[i];
            vector<vector<int>>dp(queries.size(),vector<int>(ele+1,-1));

            ans = max(ans,dpf(0,nums[i],i,dp,queries));
        }
        if(ans==INT_MAX) return -1;
        return ans;
    }
};

