class Solution {
public:
    bool check(int mid, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> v(n);
        for (int j=0;j<mid;j++) 
        {
            int l=queries[j][0], r=queries[j][1], val=queries[j][2];
            for (int i=l;i<=r;i++) 
            {
                if (i>=0 && i<n) 
                {
                    v[i].push_back(val);
                }
            }
        }
        for (int i=0;i<n;i++) 
        {
            int k=nums[i];
            if (k==0) 
                continue;
            bitset<1001> dp;
            dp[0] = 1;
            for (int x:v[i]) {
                dp|=dp<<x;
            }
            if (!dp[k]) {
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        bool flag = true;
        for(int it : nums) 
        {
            if(it != 0) 
            {
                flag = false;
                break;
            }
        }
        if (flag) 
            return 0;
        int lo=0,hi=q+1;
        while(lo<hi) 
        {
            int mid=lo+(hi-lo)/2;
            if (check(mid,nums,queries))
                hi=mid;
            else
                lo=mid+1;
        }
        return (lo>q)?-1:lo;
    }
};

