typedef int ll;
class Solution {
public:
    ll cal(vector<vector<int>>& queries,ll val,ll p){

        ll n  =queries.size();
        vector <vector <ll>> dp(n+1,vector <ll> (val+1,n+1));
        for(ll a=0;a<=val;a++){
            for(ll i=n-1;i>=0;i--){
                if(a==0){
                    dp[i][a] = 0;
                    continue;
                }
                if(queries[i][0]>p || queries[i][1]<p){
                    dp[i][a] = dp[i+1][a];
                }
                else if(queries[i][2]==a){
                    dp[i][a] = min(dp[i][a],i+1);
                }
                else if(queries[i][2]<a){
                    dp[i][a] = min(dp[i][a],min(dp[i+1][a],dp[i+1][a-queries[i][2]]));
                }
                else{
                    dp[i][a] = min(dp[i][a],dp[i+1][a]);
                }
            }
        }
        // for(ll a=0;a<n;a++){
        //     for(ll i=0;i<=val;i++){
        //         cout<<dp[a][i]<<" ";
        //     }
        //     cout<<endl;
        // }
        // cout<<endl;
        ll x = dp[0][val];
        if(x==n+1){
            return -1;
        }
        return x;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        ll n = queries.size();
        ll ans =0;
        ll i = 0;
        for(auto x : nums){
            ll p = cal(queries,x,i);
            if(p==-1){
                return -1;
            }
            ans = max(ans,p);
            // cout<<ans<<endl;
            i++;
        }
        // cout<<endl;
        return ans;
    }
};

