class Solution {
public:
    bool help(vector<int>a, int sum){
        int n = a.size();
        if(sum==0) return true;
        vector<vector<int>> dp(n+1,vector<int>(sum+1,0));
        for(int i=0;i<=n;i++){
            dp[i][0] = 1;
        }
        for(int i=1;i<=n;i++){
            for(int j=1;j<=sum;j++){
                dp[i][j]=dp[i-1][j];
                if(a[i-1]<=j){
                    dp[i][j] = (dp[i][j])||(dp[i-1][j-a[i-1]]);
                }
            }
            if(dp[i][sum]==1) return 1;
        }
        return dp[n][sum];
    }
    int minZeroArray(vector<int>& a, vector<vector<int>>& que) {
        int n=que.size(),m=a.size();
        int low=0,high=n-1,ans=n;
        bool fuck=true;
        for(auto &it:a){
            if(it!=0) {fuck=false;break;}
        }
        if(fuck) return 0;
        while(low<=high){
            int mid=low+(high-low)/2;

            vector<vector<int>> used(m);
            for(int i=0;i<=mid;i++){
                for(int j=que[i][0];j<=que[i][1];j++){
                    used[j].push_back(que[i][2]);
                }
            }
            bool check=true;
            for(int i=0;i<m;i++){
                int sum=a[i];
                vector<int> temp=used[i];
                bool cur = help(temp,sum);

                if(!cur) {
                    check=false;
                    break;
                }
            }

            if(!check) low=mid+1;
            else {
                ans=mid;
                high=mid-1;   
            }
        }
        return ans==n?-1:ans+1;
    }
};

