class Solution {
public:
    #define ll long long

    int solve(vector<vector<int>> & arr, int val, int pos, int n, vector<vector<ll>> & dp){
        if(val == 0){
            return 0;
        }
        if(pos == arr.size()){
            return n;
        }
        if(dp[pos][val] != -1){
            return dp[pos][val];
        }
        int v1 = solve(arr,val,pos+1,n,dp);
        int v2 = n;

        if(arr[pos][0]<=val){
            v2 = max(arr[pos][1], solve(arr,val-arr[pos][0],pos+1,n,dp));
        }

        return dp[pos][val] = min(v1,v2);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<vector<int>>> arr(n);

        int pos = 0;
        for(auto it : queries){

            int l = it[0] , r = it[1];
            for(int i = l ; i<=r; i++){
                arr[i].push_back({it[2],pos+1});
            }
            pos++;
        }

        int maxi = 0;

        for(int i = 0; i<n; i++){
            int len = arr[i].size();
            if(nums[i] == 0) continue;
            vector<vector<ll>> dp(len,vector<ll>(nums[i]+1,-1));

            int val = solve(arr[i],nums[i],0,queries.size()+1,dp);

            maxi = max(maxi,val);
        }
        int q = queries.size();
        if(maxi == q+1){
            return -1;
        }
        else{
            return maxi;
        }
    }
};

