class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qs) {
        int n=nums.size();
        vector<bool> good(n, false);
        int rem=n;
        for(int i=0; i<n; i++)
        {
            if(nums[i]==0) good[i]=true, --rem;
        }

        int ans=0;
        if(rem==0) return ans;

        vector<set<int>> poss(n);

        for(auto &q:qs)
        {
            ++ans;
            int l=q[0], r=q[1], v=q[2];

            // cout<<l<<" - "<<r<<" - "<<v<<endl;
            for(int i=l; i<=r; i++)
            {
                // cout<<"> i = "<<i<<" - "<<poss[i].size()<<endl;
                if(good[i]) continue;
                if(poss[i].size()==0) poss[i].insert(v);
                else
                {
                    set<int> nv;
                    for(auto &el:poss[i])
                    {
                        int ns=el+v;
                        if(poss[i].count(ns)==0 && ns<=nums[i])
                        nv.insert(ns);
                    }

                    if(nv.size()>0)
                    for(auto &el:nv) poss[i].insert(el);

                    poss[i].insert(v);
                }

                if(poss[i].count(nums[i]))
                {
                    good[i]=true;
                    --rem;
                }
            }

            if(rem==0) return ans;
        }

        return -1;
    }
};

