import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int m = queries.length;

        int left = 0, right = m, ans = -1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (canZero(nums, queries, mid)) {
                ans = mid;         
                right = mid - 1;
            } else {
                left = mid + 1;    
            }
        }
        return ans;
    }

    private boolean canZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            if (!canZeroAtIndex(nums[i], i, queries, k))
                return false;
        }
        return true;
    }

    private boolean canZeroAtIndex(int target, int i, int[][] queries, int k) {
        if (target == 0) return true;

        BitSet dp = new BitSet(target + 1);
        dp.set(0); 

        for (int j = 0; j < k; j++) {
            int l = queries[j][0], r = queries[j][1], v = queries[j][2];
            if (l <= i && i <= r) {
                BitSet shifted = shift(dp, v, target);
                dp.or(shifted);
                if (dp.get(target)) break;
            }
        }
        return dp.get(target);
    }

    private BitSet shift(BitSet bs, int v, int max) {
        BitSet shifted = new BitSet(max + 1);
        for (int s = bs.nextSetBit(0); s >= 0 && s <= max - v; s = bs.nextSetBit(s + 1)) {
            shifted.set(s + v);
        }
        return shifted;
    }
}

