class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        boolean[] isChecked = new boolean[nums.length];
        Map<Integer, Set<Integer>> map = new HashMap<>();
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] == 0) {
                isChecked[i] = true;
            }
            Set<Integer> se = new HashSet<>();
            se.add(0);
            map.put(i, se);
        }
        if (isFinsihed(isChecked)) {
            return 0;
        }        
        for (int i = 0; i < queries.length; i++) {
            int left = queries[i][0];
            int right = queries[i][1];
            int val = queries[i][2];
            for (int p = left; p <= right; p++) {
                if (isChecked[p]) {
                    continue;
                }
                if (helper(map, p, val, nums[p])) {
                    isChecked[p] = true;
                }
            }
            if (isFinsihed(isChecked)) {
                return i + 1;
            }
        }
        return -1;
    }
    public boolean helper(Map<Integer, Set<Integer>> map, int pos, int val, int target) {
        Set<Integer> set = map.get(pos);
        Set<Integer> set2 = new HashSet<>();
        for (int i : set) {
            int next = i + val;
            if (target == next) {
                return true;
            }
            if (!set.contains(next)) {
                set2.add(next);
            }
        }
        for (int i : set2) {
            set.add(i);
        }
        return false;
    }    

    public boolean isFinsihed(boolean[] checked) {
        for (int i = 0; i < checked.length; i++) {
            if (!checked[i]) {
                return false;
            }
        }
        return true;
    }
}

