class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int n = nums.size(), q = queries.size();

        if (std::accumulate(nums.begin(), nums.end(), 0) == 0) {
            return 0;
        }

        int l = 1, r = q;
        int ans = -1;
        while (l <= r) {
            int mid = (l + r) / 2;

            std::vector<std::vector<int>> a(n + 1);
            for (int i = 0; i < mid; ++i) {
                for (int x = queries[i][0]; x <= queries[i][1]; ++x) {
                    a[x].push_back(queries[i][2]);
                }
            }

            bool ok = true;
            for (int i = 0; i < n && ok; ++i) {
                std::vector<bool> dp(nums[i] + 1);
                dp[0] = true;
                for (const int &v: a[i]) {
                    for (int j = nums[i] - v; j >= 0; --j) {
                         dp[j + v] = dp[j + v] || dp[j];   
                    }
                }

                ok = dp[nums[i]];
            }

            if (ok) {
                ans = mid;
                r = mid - 1;
            }
            else {
                l = mid + 1;
            }
        }

        return ans;
    }
};

