class Solution {
    bool isSubset(vector<int>& arr,int sum){
        int n = arr.size();
        vector<bool>dp1(sum+1,0);
        vector<bool>dp2(sum+1,0);
        for(int i = 1;i<=n;i++){
            for(int t = 1;t<=sum;t++){
                if(t < arr[i-1])
                    dp2[t] = dp1[t];
                else if(t == arr[i-1] || dp1[t-arr[i-1]]) dp2[t] = 1;
            }
            if(dp2[sum]) return 1;
            dp1 = dp2;
        }
        return dp1[sum];
    }
    bool check(vector<int>& nums,vector<vector<int>>& q,int k){
        int n = nums.size();
        vector<vector<int>> sums(n);
        for(int i = 0;i<=k;i++){
            for(int j = q[i][0];j<=q[i][1];j++){
                sums[j].push_back(q[i][2]);
            }
        }
        for(int i = 0;i<n;i++){
            if(!nums[i]) continue;
            // cout<<nums[i]<<" "<<isSubset(sums[i],nums[i])<<endl;
            if(!isSubset(sums[i],nums[i])) return false;
        }
        return true;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int i = 0,j = queries.size()-1,res = -2;
        bool b = true;
        for(auto& i : nums) if(i) {b = false;break;}
        if(b) return 0;
        while(i<=j){
            int m = (i+j) >> 1;
            if(check(nums,queries,m)) res = m,j = m-1;
            else i = m+1;
            // cout<<endl;
        }
        return res+1;
    }
};

