class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        N = len(nums)
        Q = len(queries)

        if sum(nums) == 0:
            return 0

        table = [set() for _ in range(N)]
        for i in range(N):
            table[i].add(0)

        for qi, (l, r, val) in enumerate(queries):
            for i in range(l, r+1):
                s = table[i]
                lim = nums[i]
                for x in s.copy():
                    if x + val <= lim:
                        s.add(x + val)
            # check
            if all(nums[i] in table[i] for i in range(N)):
                return qi + 1

        return -1

