class Solution {
private:
    bool compute(vector<int>& arr, int index, int csum, vector<vector<int>>& dp) {
        if(csum == 0)
            return true;
        if(csum < 0)
            return false;
        if(index >= (int)arr.size())
            return false;
        if(dp[index][csum] != -1)
            return dp[index][csum];
        bool a1 = compute(arr, index+1, csum, dp);
        bool a2 = compute(arr, index+1, csum-arr[index], dp);
        return dp[index][csum] = (a1||a2);
    }
    bool canformsum(vector<int>& arr, int target) {
        int n = arr.size();
        vector<vector<int>> dp(n, vector<int>(target +1, -1));
        return compute(arr, 0, target, dp);
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int n = nums.size();
        vector<vector<int>> opsum(n, vector<int>());

        function<bool(int)> possible = [&](int steps) -> bool {
            for(int i=0;i<n;i++)
                opsum[i].clear();
            for(int k=0;k<steps;k++) {
                for(int j=queries[k][0];j<=queries[k][1];j++)
                    opsum[j].push_back(queries[k][2]);
            }
            for(int i=0;i<n;i++) {
                if(!canformsum(opsum[i], nums[i]))
                    return false;
            }
            return true;
        };

        int start = 0;
        int end = queries.size();
        int ans = -1;
        while(start <= end) {
            int mid = (start + end)/2;
            if(possible(mid)) {
                ans = mid;
                end = mid -1;
            } else {
                start = mid+1;
            }
        }
        return ans;
    }
};

