class Solution {
public:
    int help(int num , int i ,vector<pair<int,int>>&arr , vector<vector<int>>&dp) {
         int n = arr.size();
         if(num == 0) {
              if(i > 0)return arr[i - 1].second;
              return -1;
         }
         if(i == n || num < 0){
              return 10000;
         }

         if(dp[i][num] != -1)return dp[i][num];
         int ans =  min(help(num - arr[i].first , i + 1 , arr , dp) ,
                           help(num , i + 1 , arr , dp));
        return dp[i][num] = ans;
    }
    int minZeroArray(vector<int>& arr, vector<vector<int>>& q) {

         int n = arr.size();
         vector<vector<pair<int,int>>>v(n);
         for(int i = 0 ; i < q.size() ; i++ ) {
             int l = q[i][0] , r= q[i][1] , val = q[i][2];
             for(int j = l ; j <= r ; j++ ) {
                  v[j].push_back({val , i});
             }
         }
         int ans = -1;
        for(int i = 0 ; i < n ; i++ ) {
             if(arr[i] == 0) {
                  ans = max(ans , -1);
                  continue;
             }
             vector<vector<int>>dp(v[i].size() , vector<int>(arr[i] + 1 , -1));

             ans = max(ans , help(arr[i] , 0 , v[i] , dp));
        }
        if(ans >= 10000)return -1;
        return ans + 1;

    }
};

