class Solution {
public:
    bool find(const vector<int>& nums, const vector<vector<int>>& queries, int k) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) continue;
            vector<int> arr;
            for (int j = 0; j < k; j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if (l <= i && i <= r) {
                    arr.push_back(val);
                }
            }
            vector<bool> dp(nums[i] + 1, false);
            dp[0] = true;
            for (int v : arr) {
                for (int s = nums[i]; s >= v; s--) {
                    if (dp[s - v]) dp[s] = true;
                }
            }
            if (!dp[nums[i]]) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int n = nums.size();
        bool allZero = true;
        for (int x : nums) {
            if (x != 0) { allZero = false; break; }
        }
        if (allZero) return 0;
        int left = 1, right = q, ans = -1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (find(nums, queries, mid)) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return ans;
    }
};

