use std::collections::*;

impl Solution {
    pub fn min_zero_array(nums: Vec<i32>, queries: Vec<Vec<i32>>) -> i32 {
        let mut ans = 0;

        for (i, num) in nums.into_iter().enumerate() {
            if num == 0 {
                continue;
            }

            let coeffs = queries
                .iter()
                .map(|q| if q[0] as usize <= i && i <= q[1] as usize {
                    q[2]
                } else {
                    0
                })
                .collect::<Vec<_>>();

            // coeffs * bin = num
            let mut set = HashSet::new();
            set.insert(0);
            let mut idx = 0;
            'outer: while idx < coeffs.len() {
                let mut new_set = set.clone();
                for vi in set {
                    if vi + coeffs[idx] == num {
                        ans = ans.max(idx as i32 + 1);
                        break 'outer;
                    }

                    if vi + coeffs[idx] < num {
                        new_set.insert(vi + coeffs[idx]);
                    }
                }

                set = new_set;
                idx += 1;
            }

            if idx == coeffs.len() {
                return -1;
            }
        }

        ans
    }
}

