class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        List<int[]> list = new ArrayList<>();
        for (int i = 0; i < queries.length; i++)    {
            list.add(new int[] {i, queries[i][0], 1, queries[i][2]});
            list.add(new int[] {i, queries[i][1] + 1, -1, queries[i][2]});
        }
        Collections.sort(list, (a, b) -> a[1] == b[1] ? a[2] - b[2] : a[1] - b[1]);
        TreeMap<Integer, int[]> records = new TreeMap<>();
        int j = 0;
        Map<Integer, List<int[]>> m = new HashMap<>();
        for (int i = 0; i < nums.length; i++)   {
            while (j < list.size() && list.get(j)[1] <= i)  {
                int[] cur = list.get(j);
                if (cur[2] == -1)   {
                    records.remove(cur[0]);
                    j++;
                    continue;
                }

                records.put(cur[0], new int[] {cur[0], cur[3]});
                j++;
            }

            for (Map.Entry<Integer, int[]> entry : records.entrySet())  {
                int idx = entry.getKey();
                int[] arr = entry.getValue();
                m.computeIfAbsent(i, x -> new ArrayList<>()).add(arr);
            }
        }

        int[] minIdx = new int[nums.length];
        Arrays.fill(minIdx, queries.length);
        for (int i = 0; i < nums.length; i++)   {
            if (nums[i] == 0)   {
                minIdx[i] = 0;
                continue;
            }
            List<int[]> cur = m.getOrDefault(i, new ArrayList<>());
            Set<Integer> set = new HashSet<>();
            set.add(0);
            int idx = -1;
            for (j = 0; j < cur.size(); j++)    {
                Set<Integer> newSet = new HashSet<>();
                for (int s : set)
                    newSet.add(s + cur.get(j)[1]);
                newSet.addAll(set);
                set = newSet;
                if (set.contains(nums[i]))    {
                    idx = cur.get(j)[0];
                    break;
                }
            }
            if (idx == -1)
                return -1;
            minIdx[i] = idx + 1;
        }

        int ans = -1;
        for (int i : minIdx)
            ans = Math.max(ans, i);

        return ans;
    }
}

