class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        ll n = sz(nums);
        ll m = sz(queries);
        vector<vector<ll>> a(n);

        loop(j, m) {
            looping(i, queries[j][0], queries[j][1] + 1) {
                a[i].pb(j);
            }
        }

        ll ans = 0;

        loop(i, n) {
            vector<ll> b = a[i];

            unordered_map<ll, ll> dp;
            dp[0] = -1;

            trav(j, b) {
                unordered_map<ll, ll> temp;

                trav(u, dp) {
                    if (u.ff + queries[j][2] > nums[i]) {
                        continue; 
                    }
                    if (temp.find(u.ff + queries[j][2]) == temp.end()) {
                        temp[u.ff + queries[j][2]] = j;
                    }
                }

                if (temp.find(queries[j][2]) == temp.end()) {
                    temp[queries[j][2]] = j;
                }

                trav(u, temp) {
                    if (dp.find(u.ff) == dp.end()) {
                        dp[u.ff] = u.ss;
                    }
                }
            }

            if (dp.find(nums[i]) == dp.end()) {
                return -1;
            }

            ll cur = dp[nums[i]] + 1;
