import java.math.BigInteger;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int[][] realQuery = new int[nums.length][queries.length];
        for (int[] rq : realQuery) Arrays.fill(rq, 0);
        for (int i = 0; i < queries.length; i++)
            for (int j = queries[i][0]; j <= queries[i][1]; j++)
                if (queries[i][2] <= nums[j])
                    realQuery[j][i] = queries[i][2];
        int ans = 0;
        for (int i = 0; i < nums.length; i++)
            ans = Math.max(ans, minSteps(nums[i], realQuery[i]));
        if (ans == Integer.MAX_VALUE) return -1;
        return ans;
    }

    private int minSteps(int target, int[] query) {
        BigInteger set = BigInteger.ONE;
        for (int i = 0; i < query.length; i++) {
            if (set.testBit(target))
                return i;
            set = set.shiftLeft(query[i]).or(set);
        }
        if (set.testBit(target))
            return query.length;
        return Integer.MAX_VALUE;
    }
}

