        def rec(arr, target, index, memo):

            if target == 0:
                return True
            if target < 0 or index >= len(arr):
                return False

            if (target, index) in memo:
                return memo[(target, index)]

            a = rec(arr, target - arr[index], index + 1, memo)

            b = rec(arr, target, index+1, memo)

            memo[(target, index)] = a or b
            return memo[(target, index)]


        def see(took):
            prefix = [[] for _ in range(len(nums))]
            for i in range(took):
                q = queries[i]
                start, end, val = q
                for i in range(start, end+1):
