            for i in range(len(nums)):
                memo = {}
                #print(took, prefix[i], nums[i])
                res = rec(prefix[i], nums[i], 0, memo)

                #print(res)
                if not res:
                    return False

            return True


        left = 0
        right = len(queries)
        while left <= right:
            mid = (left+right)//2
            res = see(mid)
            if not res:
                left = mid+1
            else:
                right = mid-1
        if left == len(queries)+1:
            return -1
        return left

