}
    bool[] dp = new bool[num + 1];
    dp[0] = true;
    int first = 0;
    foreach (var query in queries)
    {
        first++;
        int start = query[0];
        int end = query[1];
        int value = query[2];
        if (start > index || end < index)
        {
            continue;
        }

        for(int i = dp.Length - 1; i >= value; i--)
        {
            dp[i] = dp[i] || dp[i - value];
        }

        if (dp[num])
        {
            return first;
        }
    }

    return -1;
}
