    int dfs(int n, int i, int ind, vector<int>& nums, vector<vector<int>>& q, int m){
        if(n==0){
            return i;
        }
        if(i==m){
            return INT_MAX;
        }
        if(dp[n][i]!=-1){
            return dp[n][i];
        }
        if(q[i][0]<=ind && q[i][1]>=ind && n>=q[i][2]){
            return dp[n][i] = min(dfs(n, i+1, ind, nums, q, m), 
                                    dfs(n-q[i][2], i+1, ind, nums, q, m));
        }
        return dp[n][i] = dfs(n, i+1, ind, nums, q, m);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size(), m= q.size();
