class Solution {
public:
    bool can(vector<vector<int>>& q,int k,int cur,int i,vector<vector<int>>& dp){
        if(cur<0) return 0;
        if(cur==0) return 1;
        if(k==-1) return 0;
        if(dp[k][cur]!=-1) return dp[k][cur]; 
        int l=q[k][0];
        int r=q[k][1];
        int v=q[k][2];
        bool z=0;
        if(i>=l&&i<=r){
            z=can(q,k-1,cur-v,i,dp);
        }
        z=max(z,can(q,k-1,cur,i,dp));
        return dp[k][cur]=z;
    }
    bool ok(int mid, vector<int>& nums, vector<vector<int>>& q, int n, int m) {
        // cout<<mid<<endl;
        for(int i=0;i<n;i++){
            vector<vector<int>> dp(mid+1,vector<int>(nums[i]+1,-1));
            if(!can(q,mid-1,nums[i],i,dp)) return 0;
        }
        return 1;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int l = 0, r = q.size(), n = nums.size();
        int m = q.size();
        while (l < r) {
            int mid = l + (r - l) / 2;
            // cout<<mid<<endl;
            if (ok(mid, nums, q, n, r))
                r = mid;
            else
                l = mid + 1;
        }
        // cout<<r<<endl;
        return (ok(r, nums, q, n, m)) ? r : -1;
    }
};

