                for (int value : set) {
                    set2.add(value + v);
                }
                set.addAll(set2);
            }
            boolean valid = true;
            for (int j = 0; j < n; j++) {
                if (!sets.get(j).contains(nums[j])) {
                    valid = false;
                    break;
                }
            }
            if (valid) return i + 1;
        }
        return -1;
    }

    boolean isAllZero(int[] arr) {
        for (int v : arr) {
            if (v != 0) return false;
        }
        return true;
    }
