class Solution
{
    using vi = vector<int>;
    using vvi = vector<vi>;

    bool f(int n, int k, vi &arr, vvi &memo)
    {
        if (k == 0)
            return 1;
        if (n == 0)
            return 0;
        // base condition

        if (memo[n][k] != -1)
            return memo[n][k];

        bool pick = false, notPick = false;
        if (arr[n - 1] <= k)
            pick = f(n - 1, k - arr[n - 1], arr, memo);
        notPick = f(n - 1, k, arr, memo);
        return memo[n][k] = (pick | notPick);
    }

    // 10
    // 1 2 4 5 6 3 9 8

    // 1->Yes   k = 9

    bool susumK(int n, int k, vector<int> &arr)
    {
        if (n == 0)
            return k == 0;
        vvi memo(n + 1, vi(k + 1, -1));
        return f(n, k, arr, memo);
    }

public:
