    {
        int n = nums.size();
        int countZero = count(nums.begin(), nums.end(), 0);
        if (countZero == n)
            return 0;
        int left = 1, right = q.size();
        int ans = -1;
        while (left <= right)
        {
            int mid = left + (right - left) / 2;
            vvi v(n);
            for (int i = 0; i < mid; i++)
            {
                int l = q[i][0], r = q[i][1], x = q[i][2];
                for (int j = l; j <= r; j++)
                    v[j].push_back(x);
            }
            bool ok = true;
            for (int i = 0; i < n; i++)
            {
                if (!susumK(v[i].size(), nums[i], v[i]))
                {
                    ok = false;
                    break;
                }
            }
            if (ok)
                ans = mid, right = mid - 1;
            else
                left = mid + 1;
        }
        return ans;
    }
};

