class Solution {
public:
    bool isSubsetSum(vector<int>& arr, int sum) {
        int n = arr.size();
        vector<vector<int>> dp(n + 1, vector<int>(sum + 1, 0));
        for (int i = 0; i <= n; i++) {
            dp[i][0] = 1;
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= sum; j++) {
                dp[i][j] = dp[i - 1][j];
                if (j >= arr[i - 1]) {
                    dp[i][j] = dp[i][j] || dp[i - 1][j - arr[i - 1]]; 
                }
            }
        }
        return dp[n][sum];
    }

    bool isValid(int k, vector<int>& a, vector<vector<int>>& q) {
        vector<vector<int>> temp(a.size());
        for (int i = 0; i < k; i++) {
            int l = q[i][0];
            int r = q[i][1];
            int val = q[i][2];
            for (int j = l; j <= r; j++) {
                temp[j].push_back(val);
            }
        }
        for (int i = 0; i < a.size(); i++) {
            if (!isSubsetSum(temp[i], a[i])) {
                return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int low = 0, high = q.size(), ans = 1e9;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (isValid(mid, a, q)) {
                ans = min(ans,mid);
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        if(ans==1e9) return -1;
        return ans;
    }
};

