class Solution {
public:
    int solve(int i, vector<vector<int>>& queries, int idx, int x, vector<vector<int>> &dp){
        if(x == 0){
            return i;
        }
        if(i == queries.size()){
            return INT_MAX;
        }
        if(dp[i][x] != -1){
            return dp[i][x];
        }

        int l = queries[i][0], r = queries[i][1], val = queries[i][2];
        if(idx >= l && idx <= r){
            int pick = INT_MAX, notPick = solve(i+1, queries, idx, x, dp);
            if(x >= val){
                pick = solve(i+1, queries, idx, x-val, dp);
            }

            return dp[i][x] = min(pick, notPick);
        }
        else{
            return dp[i][x] = solve(i+1, queries, idx, x, dp);
        }
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int ans = INT_MIN, q = queries.size();
        for(int i = 0; i < n; i++){
            int x = nums[i];
            vector<vector<int>> dp(q, vector<int>(x+1,-1));
            int tempAns = solve(0, queries, i, x, dp);
            // cout << tempAns << endl;
            if(tempAns == INT_MAX) return -1;
            ans = max(ans, tempAns);
        }
        return ans;
    }
};

