    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        bool allZero = true;
        for (int i = 0; i < v.size(); i++) if (v[i] != 0) allZero = false;
        if (allZero) return 0;

        int res = 0;
        for (int i = 0; i < v.size(); i++) {
            if (v[i] == 0) continue;
            vector<int> vv, idx;
            for (int j = 0; j < q.size(); j++) {
                if (q[j][0] <= i && q[j][1] >= i) {
                    vv.push_back(q[j][2]);
                    idx.push_back(j + 1);
                }
            }

            int dp1[1001] = {0};
            dp1[0] = 1;
            bool found = false;
            for (int j = 0; j < vv.size(); j++) {
                for (int k = 0; k + vv[j] <= v[i]; k++) {
                    if (dp1[k] == 1 && dp1[k + vv[j]] == 0) {
                        dp1[k + vv[j]] = 2;
                    }
                }
                for (int k = 0; k <= v[i]; k++) if (dp1[k] == 2) dp1[k] = 1;
                if (dp1[v[i]] == 1) {
                    res = max(res, idx[j]);
                    found = true;
                    break;
                }
            }
            if (!found) {
                res = -1;
                break;
            }
        }
        return res;

    }
