class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>&q) {
        int n=nums.size();
        int sz=q.size();
        bool flag=true;
        for(auto num:nums){
            if(num){
                flag=false;
                break;
            }
        }
        if(flag)return 0;
        int s=1, e=sz;
        vector<vector<bool>>index(n+1,vector<bool>(1005,false));
        for(int i=0;i<n;i++)index[i][0]=true;
        while(s<=e){
            int mid=s+(e-s)/2;
            for(int i=0;i<mid;i++){
                int l=q[i][0], r=q[i][1], val=q[i][2];
                while(l<=r){
                    auto tmp=index[l];
                    for(int j=0;j<=(nums[l]-val);j++){
                        if(index[l][j]){
                            tmp[j+val]=true;
                        }
                    }
                    index[l]=tmp;
                    l++;
                }
            }
            bool flag=true;
            for(int idx=0;idx<n;idx++){
                if(!index[idx][nums[idx]]){
                    flag=false;
                }
                for(int j=1;j<=nums[idx];j++)index[idx][j]=false;
            }
            if(flag)e=mid-1;
            else s=mid+1;
        }
        if(s>sz)return -1;
        return s;
    }
};

