class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        result = 0

        for n_pos in range (len(nums)):
            num = nums[n_pos]
            if num == 0:
                continue

            can_decrease = [False] * (num + 1)
            can_decrease[0] = True

            for q_pos in range(len(queries)):
                q = queries[q_pos]
                if q[0] > n_pos or q[1] < n_pos:
                    continue

                for i in range (num, q[2] - 1, -1):
                    if can_decrease[i - q[2]]:
                        can_decrease[i] = True

                if can_decrease[num]:
                    result = max(result, q_pos+1)
                    break

            if not can_decrease[num]:
                return -1

        return result

